mapper.select:
select id, username, name, password, sex, authority
from user

    and id = #{id}
     and username = #{username}
     and name = #{name}
     and password = #{password}
     and sex = #{sex}
     and authority = #{authority}

select count(sid)
from score
where uid = #{userId}

select type, num, time, timeStamp, finalScore
from score
where uid = #{userId}

mapper.update:
update user
name = #{name}, username = #{username}, password = #{password}, sex = #{sex}, authority = #{authority},
where id = #{id}
